import React, { useState, useEffect, useCallback } from 'react';
import { Link } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import { BsFillPencilFill, BsFillPlusCircleFill } from "react-icons/bs";
import "./Records.css";


const Records = ({
    user,
    records,
    setRecords,
    recordList,
    setRecordList,
    currentPage,
    setCurrentPage,
    entriesPerPage,
    totalResults,
    setEntriesPerPage,
    getRecords,
    setTotalResults
}) => {

    return (
        <div className="App">
            <Container className="main-container">
                <Row>
                    < Link to={"/records/edit"} >
                        <BsFillPlusCircleFill/> Add
                    </Link>

                </Row>
                <Row>
                    <Col>Date</Col>
                    <Col>Food(g)</Col>
                    <Col>Water(ml)</Col>
                    <Col>Poop(times)</Col>
                    <Col>Walk(min)</Col>
                    <Col>Vaccine(y/n)</Col>
                    <Col>Medicine(y/n)</Col>
                    <Col>Grooming(y/n)</Col>
                    <Col>Other</Col>
                    <Col></Col>
                    </Row>
                {/* {records.map((record) => {
                        return (
                <Row key={record._id}>
                    <Col>{record.date}</Col>
                    <Col>{record.food}</Col>
                    <Col>{record.water}</Col>
                    <Col>{record.poop}</Col>
                    <Col>{record.walk}</Col>
                    <Col>{record.vaccine}</Col>
                    <Col>{record.medicine}</Col>
                    <Col>{record.grooming}</Col>
                    <Col>{record.other}</Col>
                    <Col><Link to={"/records/edit"} state={{ currentRecord: record }}>

                                            <BsFillPencilFill/>
                                        </Link></Col>

                </Row>
                )})}

                 */}

                <Row className="movieRow">

                    {records.map((record) => {
                        return (
                            <Col key={record._id}>
                                <Card className="moviesListCard">
                                    <Card.Body>
                                        <Card.Title>{record.date}</Card.Title>
                                        <Card.Text>
                                        <img src="/images/bone.jpg" className="recordIcon" />&nbsp;{record.food} &nbsp;&nbsp;

                                        <img src="/images/water.png" className="recordIcon" />&nbsp;{record.water} &nbsp;&nbsp;

                                        <img src="/images/poop.png" className="recordIcon" />&nbsp;{record.poop} &nbsp;&nbsp;

                                        <img src="/images/walk.png" className="recordIcon" /> &nbsp;{record.walk} &nbsp;&nbsp;
                                        <img src="/images/vaccine.png" className="recordIcon" /> &nbsp;{record.vaccine} &nbsp;&nbsp;
                                       <img src="/images/medicine.png" className="recordIcon" />&nbsp;{record.medicine} &nbsp;&nbsp;
                                        <img src="/images/grooming.jpg" className="recordIcon" />&nbsp;{record.grooming}&nbsp;&nbsp;
                                      <img src="/images/memo.png" className="recordIcon" />&nbsp;{record.other}&nbsp;&nbsp;&nbsp;&nbsp;
                                      </Card.Text>

                                        <Link  to={"/records/edit"} state={{ currentRecord: record }}>

                                            <BsFillPencilFill/>
                                        </Link>

                                    </Card.Body>
                                </Card>
                            </Col>

                        )
                    })}
                </Row>
                <br />
                {currentPage!=0&&<Button
                    variant="link"
                    onClick={() => { setCurrentPage(currentPage - 1) }}
                >
                    Get previous {entriesPerPage} results
                </Button>}

                Showing page:{currentPage + 1}.
                {entriesPerPage*(currentPage + 1)<totalResults&&<Button
                    variant="link"
                    onClick={() => { setCurrentPage(currentPage + 1) }}
                >
                    Get next {entriesPerPage} results
                </Button>}
            </Container>
        </div>
    )
}


export default Records;
