import React, { useState, useEffect, useCallback } from 'react';
import { Link } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import { BsFillPencilFill, BsFillPlusCircleFill } from "react-icons/bs";
import "./Records.css";


const Records = ({
    user,
    records,
    setRecords,
    recordList,
    setRecordList,
    currentPage,
    setCurrentPage,
    entriesPerPage,
    setEntriesPerPage,
    getRecords
}) => {

    return (
        <div className="App">
            <Container className="main-container">
                <Row>
                    <Link to={"/records/edit"} >
                        {BsFillPlusCircleFill}
                    </Link>
                </Row>
                <Row className="movieRow">

                    {records.map((record) => {
                        return (
                            <Col key={record._id}>
                                <Card className="moviesListCard">
                                    <Card.Body>
                                        <Card.Title>{record.date}</Card.Title>
                                        <Card.Text>
                                            Food:{record.food} g
                                        </Card.Text>
                                        <Card.Text>
                                            Water:{record.water} ml
                                        </Card.Text>
                                        <Card.Text>
                                            Poop:{record.poop} times
                                        </Card.Text>
                                        <Card.Text>
                                            Walk:{record.water} mins
                                        </Card.Text>
                                        <Card.Text>
                                            Vaccine:{record.vaccine}
                                        </Card.Text>
                                        <Card.Text>
                                            Medicine:{record.medicine}
                                        </Card.Text>
                                        <Card.Text>
                                            Grooming:{record.grooming}
                                        </Card.Text>
                                        <Card.Text>
                                            Other:{record.other}
                                        </Card.Text>

                                        <Link to={"/records/edit"} state={{ currentRecord: record }}>
                                            {BsFillPencilFill}
                                        </Link>
                                    </Card.Body>
                                </Card>
                            </Col>

                        )
                    })}
                </Row>
                <br />
                Showing page:{currentPage + 1}.
                <Button
                    variant="link"
                    onClick={() => { setCurrentPage(currentPage + 1) }}
                >
                    Get next {entriesPerPage} results
                </Button>
            </Container>
        </div>
    )
}


export default Records;
