import React,{useState} from 'react';
import MovieDataService from "../services/movies";
import {useNavigate,useParams,useLocation} from "react-router-dom";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';

const AddReview=({user})=>{
    const navigate=useNavigate();
    let params=useParams();
    let editing=false;
    let initialReviewState="";
    let location=useLocation();
    // console.log(location.state);
    // console.log("locat",location);
    if (location.state!==null) {
        editing=true;
        initialReviewState=location.state.currentReview.review;
        // console.log("QQ",location.state.currentReview);
    }
    // console.log(editing);

    const [review,setReview]=useState(initialReviewState);

    const onChangeReview=e=>{
        // console.log("PPP");
        const review=e.target.value;
        setReview(review);
    }

    const saveReview=()=>{
        var data={
            review:review,
            name:user.name,
            user_id:user.googleId,
            movie_id:params.id
        }

        if(editing){
            var reviewData={
                review:review,
                name:user.name,
                user_id:user.googleId,
                movie_id:params.id,
                review_id:location.state.currentReview._id
            }
            // console.log("editing")
            //TODO:Handle case where an existing review is being updated
            MovieDataService.editReview(reviewData)
            .then(response=>{
                navigate("/movies/"+params.id);
            })
            .catch(e=>{
                console.log(e);
            });
        }else{
            MovieDataService.createReview(data)
            .then(response=>{
                navigate("/movies/"+params.id);
            })
            .catch(e=>{
                console.log(e);
            });
        }
    }

    return(
        <Container className="main-container">
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>{editing?"Edit":"Create"} Review</Form.Label>
                    <Form.Control
                    as="textarea"
                    type="text"
                    required
                    review={review}
                    onChange={onChangeReview}
                    defaultValue={editing?initialReviewState:""}
                    />
                </Form.Group>
                <button variant="primary" onClick={saveReview}>
                    Submit
                </button>
            </Form>
        </Container>
    )
}

export default AddReview;
