// },[]);



  useEffect(() => {
    if(user){
      console.log("re")
        retreiveFavorites(user);
      }
    getMovies(favorites);
  },[]);

  useEffect(() => {
    console.log("@@",favorites)
    getMovies(favorites);
  },[favorites]);

  useEffect(() => {
    console.log("1",favorites)

  },[favorites]);

  useEffect(() => {
    if(user){
    let i;
    let newFavIds=[];
    for(i=0;i<cards.length;i++){
      newFavIds.push(cards[i]._id);
    }
    console.log("new",newFavIds);
    let data={
      _id:user.googleId,
      favorites:newFavIds
    };
    uploadFavorites(data);
  }
  console.log("fav",favorites)
  console.log("cards",cards)
  },[cards]);


    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]],
          ],
        }),
      )
    }, [])
    const renderCard = useCallback((card, index) => {
      return (
        <DnDCard
          key={card._id}
          index={index}
          id={card._id}
          poster={card.poster}
          text={card.title}
          moveCard={moveCard}
        />
      )
    }, []);

    return (
      <>
        <div style={style}>{cards.map((card, i) => renderCard(card, i))}</div>
       {console.log("qq",cards)}
      </>
    )

}
