import React, { useState } from 'react';
import MovieDataService from "../services/Records";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import "./EditInfo.css";

const EditInfo = ({
    user,
    getInfo,
    updateInfo,
    setName,
    setBreed,
    setGender,
    setBirthday,
    name,
    breed,
    gender,
    birthday
}) => {
    const navigate = useNavigate();
    const onChangeName = e => {
        const name = e.target.value;
        setName(name);
    }

    const onChangeBreed = e => {
        const breed = e.target.value;
        setBreed(breed);
    }

    const onChangeGender = e => {
        const gender = e.target.value;
        setGender(gender);
    }

    const onChangeBirthday = e => {
        const birthday = e.target.value;
        setBirthday(birthday);
    }

    const saveProfile = () => {
        var data = {
            name: name,
            _id: user.googleId,
            breed: breed,
            gender: gender,
            birthday: birthday
        }
        console.log("save",data)
        updateInfo(data)
        console.log("done")
        navigate("/info");



    }

    return (
        <Container className="">

            <Form>
                <Form.Group className="mb-3">
                {/* <Form.Row style={{width: "100%", marginBottom: "20px"}}> */}
                   <Row>
                    <Col>
                    <Form.Label className={"formAlign"}>name
                    </Form.Label>
                    </Col>
                    <Col>
                   <Form.Control
                        as="textarea"
                        type="text"
                        required
                        name={name}
                        onChange={onChangeName}
                        defaultValue={name}
                        className={"formAlign"}
                    />
                    </Col>
                   {/* </Form.Row > */}
                   </Row>
                    <Form.Label className={"formAlign"}>breed</Form.Label>
                    <Form.Control
                        as="textarea"
                        type="text"
                        required
                        breed={breed}
                        onChange={onChangeBreed}
                        defaultValue={breed}
                    />

                    <Form.Label className={"formAlign"}>gender</Form.Label>
                    <Form.Control
                        as="textarea"
                        type="text"
                        required
                        gender={gender}
                        onChange={onChangeGender}
                        defaultValue={gender}
                    />

                    <Form.Label className={"formAlign"}>birthday</Form.Label>
                    <Form.Control
                        as="textarea"
                        type="text"
                        required
                        birthday={birthday}
                        onChange={onChangeBirthday}
                        defaultValue={birthday}

                    />
                </Form.Group>
                <Button variant="primary" onClick={saveProfile}>
                    Save
                </Button>
            </Form>
        </Container>
    )
}

export default EditInfo;
