import update from 'immutability-helper'
import { useCallback, useState, useEffect } from 'react'
import  {DnDCard}  from './DnDCard.js'
import MovieDataService from "../services/movies";

const style = {
  width: 400,
};
export const Container = ({user,uploadFavorites}) => {


  const [cards, setCards] = useState([]);
  const [favorites,setFavorites]=useState([]);
  const retreiveFavorites=useCallback((user)=>{
    var data={
      user_id:user.googleId
    }

    FavoriteDataService.getFavorites(data.user_id)
    .then(response=>{
      console.log("@@");
      setFavorites(response.data.favorites);
    })
    .catch(e=>{
        console.log(e);
    });
  },[]);
  const getMovies=ids=>{
    //TODO

    MovieDataService.getFavoritesMovies(ids.toString())
    .then(response=>{
      setCards (response.data);
    })
    .catch(e=>{
        console.log(e);
    })
}

  useEffect(() => {
    console.log("1",favorites)
    getMovies(favorites);
  },[]);

  useEffect(() => {

  console.log("++",favorites) 
    getMovies(favorites);
  },[favorites]);

  useEffect(() => {
    let i;
    let newFavIds=[];

    for(i=0;i<cards.length;i++){
      newFavIds.push(cards[i]._id);
    }
    console.log("new",newFavIds);
    let data={
      _id:user.googleId,
      favorites:newFavIds
    };
    uploadFavorites(data);
  },[cards]);


    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]],
          ],
        }),
      )
    }, [])
    const renderCard = useCallback((card, index) => {
      return (
        <DnDCard
          key={card._id}
          index={index}
          id={card._id}
          text={card.title}
          moveCard={moveCard}
        />
      )
    }, []);

    return (
      <>
        <div style={style}>{cards.map((card, i) => renderCard(card, i))}</div>
         {console.log("cards",cards)}
      </>
    )

}
