import { GoogleOAuthProvider } from '@react-oauth/google';
import {useEffect,useState,useCallback} from 'react';
import {Routes,Route,Link} from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Login from "./components/Login";
import Logout from "./components/Logout";
import MoviesList from "./components/MoviesList";
import Movie from "./components/Movie";
import Favorites from "./components/Favorites";
import AddReview from "./components/AddReview";
import './App.css';
import FavoriteDataService from "./services/favorites";

const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;


function App() {

const [user,setUser]=useState(null);
const [favorites,setFavorites]=useState([]);

const addFavorite=(movieId)=>{
  setFavorites([...favorites,movieId]);

}

const deleteFavorite=(movieId)=>{
  setFavorites(favorites.filter(f=>f!==movieId));
  var data={
    _id:user.googleId,
    favorites:favorites
  }
  uploadFavorites()
}

const retreiveFavorites=useCallback((user)=>{
  var data={
    user_id:user.googleId
  }

  FavoriteDataService.getFavorites(data.user_id)
  .then(response=>{
    console.log("@@");
    setFavorites(response.data.favorites);
  })
  .catch(e=>{
      console.log(e);
  });
},[]);

const uploadFavorites=useCallback((data)=>{
  console.log("upload",data)
  FavoriteDataService.updateFavorites(data)
  .catch(e=>{
      console.log(e);
  });
},[]);

useEffect(()=>{
  if(user){
  var data={
    _id:user.googleId,
    favorites:favorites
  }
  uploadFavorites(data);
  // retreiveFavorites(user);
}
},[favorites]);

useEffect(() => {
  if(user){
  retreiveFavorites(user);}
},[user,]);

useEffect(() => {
  let loginData = JSON.parse(localStorage.getItem("login"));
  if (loginData) {
    let loginExp = loginData.exp;
    let now = Date.now()/1000;
    if (now < loginExp) {
      // Not expired
      setUser(loginData);
    } else {
      // Expired
      localStorage.setItem("login", null);
    }
  }
}, []);



  return (
    <GoogleOAuthProvider clientId={clientId}>
    <div className="App">
      <Navbar bg="primary" expand="lg" sticky="top" variant="dark">
        <Container className="container-fluid">
          <Navbar.Brand className="brand" href="/">
            <img src="/images/movies-logo.png" alt="movies logo" className="moviesLogo"/>
            MOVIE TIME
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav"/>
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="ml-auto">
              <Nav.Link as={Link} to={"/movies"}>
                Movies
              </Nav.Link>
              {user&&(<Nav.Link as={Link} to={"/favorites"}>
                Favorites
              </Nav.Link>)}

            </Nav>
          </Navbar.Collapse>
          {user?(
            <Logout setUser={setUser} />

          ):(
            <Login setUser={setUser} />

          )}

        </Container>
      </Navbar>

      <Routes>
        <Route path={"/movies/:id/"} element={
          <Movie user={user}/>}
          />


          <Route path={"/movies/:id/review"} element={
            <AddReview user={user} />}
            />
        <Route exact path={"/"} element={
          <MoviesList
            user={user}
            addFavorite={addFavorite}
            deleteFavorite={deleteFavorite}
            favorites={favorites}
          />}
          />
        <Route exact path={"/movies"} element={
          <MoviesList
          user={user}
            addFavorite={addFavorite}
            deleteFavorite={deleteFavorite}
            favorites={favorites}
          />}
          />
        <Route path={"/movies/:id/"} element={
          <Movie/>}
          />
        <Route exact path={"/favorites"} element={
          <Favorites
          user={user}
          uploadFavorites={uploadFavorites}
          favorites={favorites}
          />}
          />
      </Routes>
    </div>
    </GoogleOAuthProvider>
  );
}


export default App;
