import update from 'immutability-helper'
import { useCallback, useState, useEffect } from 'react'
import  {DnDCard}  from './DnDCard.js'
import MovieDataService from "../services/movies";

const style = {
  width: 400,
};
export const Container = ({uploadFavorites,favorites}) => {

    const getMovies=ids=>{
      //TODO
      MovieDataService.getFavoritesMovies(ids)
      .then(response=>{
          return (response.data);
      })
      .catch(e=>{
          console.log(e);
      });

  }
  const [cards, setCards] = useState([]);
  useEffect(() => {
    let favoritesMovies=getMovies(favorites);
    setCards(favoritesMovies)
  },[]);

  useEffect(() => {
    let favoritesMovies=getMovies(favorites);
    setCards(favoritesMovies)
  },[favorites]);

  useEffect(() => {
    let i;
    let newFavIds=[];

    for(i=0;i<cards.length;i++){
      newFavIds.push(cards[i]._id);
    }
    uploadFavorites(newFavIds);
  },[cards]);


    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]],
          ],
        }),
      )
    }, [])
    const renderCard = useCallback((card, index) => {
      return (
        <DnDCard
          key={card._id}
          index={index}
          id={card._id}
          text={card.title}
          moveCard={moveCard}
        />
      )
    }, []);

    return (
      <>
        <div style={style}>{cards.map((card, i) => renderCard(card, i))}</div>
      </>
    )

}
