import { render } from 'react-dom'
import { DnDContainer } from './DnDContainer'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import { DndProvider } from 'react-dnd'
import { HTML5Backend } from 'react-dnd-html5-backend'
import { useCallback, useState, useEffect } from 'react'
import "./Favorites.css";

const Favorites = ({
    user,
    favorites,
    uploadFavorites,
    retreiveFavorites
}) => {
    const [cards, setCards] = useState([]);

  const getMovies = useCallback((ids) => {
    MovieDataService.getFavoritesMovies(ids.toString())
      .then(response => {
        console.log("gg", favorites)
        setCards(response.data);
      })
      .catch(e => {
        console.log(e);
      })
  }, []);

  useEffect(() => {
    if (user) {
      console.log("re")
      retreiveFavorites(user);
    }
    getMovies(favorites);
  }, []);

  useEffect(() => {
    console.log("@@", favorites)
    getMovies(favorites);
  }, [favorites]);

  useEffect(() => {
    if (user) {
      let i;
      let newFavIds = [];
      for (i = 0; i < cards.length; i++) {
        newFavIds.push(cards[i]._id);
      }
      console.log("new", newFavIds);
      let data = {
        _id: user.googleId,
        favorites: newFavIds
      };
      uploadFavorites(data);
    }
    console.log("fav", favorites)
    console.log("cards", cards)
  }, [cards]);
    return (
        <div >
            <DndProvider backend={HTML5Backend}>
                <Container className={""}>
                    <Row>
                        <Col className={"favoritesPanel col-2"}>
                            Drag your favorites to rank them
                        </Col>
                        <Col className={"favoritesContainer col-6"}>
                            <DnDContainer
                                user={user}
                                favorites={favorites}
                                uploadFavorites={uploadFavorites}
                                retreiveFavorites={retreiveFavorites}
                            />
                        </Col>
                        <Col>
                        </Col>
                    </Row>
                </Container>
            </DndProvider>
        </div>
    )
}
export default Favorites;
