import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import { Link } from "react-router-dom";
import "./Profile.css";
import { useSpring, animated } from '@react-spring/web';

const Profile = ({
    user,
    getInfo,
    updateInfo,
    setName,
    setBreed,
    setGender,
    setBirthday,
    name,
    breed,
    gender,
    birthday
}) => {

    const styles = useSpring({
        loop: true,
        to: [
          { opacity: 1, color: '#ffaaee' },
          { opacity: 0, color: 'rgb(14,26,19)' },
        ],
        from: { opacity: 0, color: 'red' },
      })

    return (
        <div >
            {/* <animated.div style={styles}>MyPet</animated.div> */}
            {!user && <animated.div style={styles}>wecome to myPet!\nPlease log in</animated.div>}
            {user && <Container >
                <Row>
                    <Col className={"favoritesPanel col-2"}>
                        name
                    </Col>
                    <Col className={"favoritesContainer col-6"}>
                        {name}
                    </Col>
                </Row>
                <Row>
                    <Col>
                        breed
                    </Col>
                    <Col>
                        {breed}
                    </Col>
                </Row>
                <Row>
                    <Col>
                        gender
                    </Col>
                    <Col>
                        {gender}
                    </Col>
                </Row>
                <Row>
                    <Col>
                        birthday
                    </Col>
                    <Col>
                        {birthday}
                    </Col>
                </Row>
                <Link to={"/mypet/info/edit" }>
                    Edit
                </Link>
            </Container>}

        </div>
    )
}
export default Profile;
