import update from 'immutability-helper'
import { useCallback, useState, useEffect } from 'react'
import  {DnDCard}  from './DnDCard.js'
import MovieDataService from "../services/movies";
import FavoriteDataService from "../services/favorites";

const style = {
  width: 600
};

  export const DnDContainer = ({user,uploadFavorites}) => {

  const [cards, setCards] = useState([]);
  const [favorites,setFavorites]=useState([]);

  useEffect(() => {
    let loginData = JSON.parse(localStorage.getItem("login"));
    if (loginData) {
      let loginExp = loginData.exp;
      let now = Date.now()/1000;
      if (now < loginExp) {
        // Not expired
        setUser(loginData);
      } else {
        // Expired
        localStorage.setItem("login", null);
      }
    }
  }, []);


  const getMovies=ids=>{
    MovieDataService.getFavoritesMovies(ids.toString())
    .then(response=>{
      setCards (response.data);}
    )
    .catch(e=>{
        console.log(e);
    })
};

const retreiveFavorites=useCallback((user)=>{
  var data={
    user_id:user.googleId
  }
  FavoriteDataService.getFavorites(data.user_id)
  .then(response=>{
    setFavorites(response.data.favorites);
  })
  .catch(e=>{
      console.log(e);
  });
},[]);



  useEffect(() => {
    if(user){
        retreiveFavorites(user);
      }
    getMovies(favorites);
  },[]);

  useEffect(() => {
    getMovies(favorites);
  },[favorites]);

  useEffect(() => {
    if(user){
    let i;
    let newFavIds=[];
    for(i=0;i<cards.length;i++){
      newFavIds.push(cards[i]._id);
    }
    console.log("new",newFavIds);
    let data={
      _id:user.googleId,
      favorites:newFavIds
    };
    uploadFavorites(data);
  }
  console.log("fav",favorites)
  },[cards]);


    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]],
          ],
        }),
      )
    }, [])
    const renderCard = useCallback((card, index) => {
      return (
        <DnDCard
          key={card._id}
          index={index}
          id={card._id}
          poster={card.poster}
          text={card.title}
          moveCard={moveCard}
        />
      )
    }, []);

    return (
      <>
        <div style={style}>{cards.map((card, i) => renderCard(card, i))}</div>

      </>
    )

}
